-- ----------------------------------------- --
-- CREATE DATABASE, USER AND SET PRIVILEGES  --
-- ----------------------------------------- --

CREATE DATABASE IF NOT EXISTS 	kroton_test
	DEFAULT CHARACTER SET 		latin5 
    COLLATE 					latin5_bin;
    
    
CREATE USER 'kroton_test'@'localhost' IDENTIFIED BY 'root';

GRANT ALL PRIVILEGES ON kroton_test.* TO 'kroton_test'@'localhost';

FLUSH PRIVILEGES;

-- ----------------------------------------- --
--           CREATE TABLES FOR TEST          --
-- ----------------------------------------- --

CREATE TABLE kroton_test.SERVICO(
  ID BIGINT(12) 			NOT NULL COMMENT 'Chave primária da tabela.' AUTO_INCREMENT,
  NOME VARCHAR(100)		NOT NULL COMMENT 'Nome do serviço.',
  PRIMARY KEY PK_SERVICO (ID),
  UNIQUE KEY UK_SERVICO_NOME (NOME)
) ENGINE=InnoDB COMMENT='Tabela para registro de seviços.';

CREATE TABLE kroton_test.STATUS(
  ID BIGINT(12) 			NOT NULL COMMENT 'Chave primária da tabela.' AUTO_INCREMENT,
  NOME VARCHAR(50) 		NOT NULL COMMENT 'Nome do status.',
  PRIMARY KEY PK_STATUS (ID),
  UNIQUE KEY UK_STATUS_NOME (NOME)
) ENGINE=InnoDB COMMENT='Tabela para registro de status.';

CREATE TABLE kroton_test.CURSO(
  ID BIGINT(12) 			NOT NULL COMMENT 'Chave primária da tabela.' AUTO_INCREMENT,
  NOME VARCHAR(50) 		NOT NULL COMMENT 'Nome do curso.',
  PRIMARY KEY PK_CURSO (ID),
  UNIQUE KEY UK_CURSO_NOME (NOME)
) ENGINE=InnoDB COMMENT='Tabela para registro de curso.';

CREATE TABLE kroton_test.USUARIO(
  ID BIGINT(12) 			NOT NULL COMMENT 'Chave primária da tabela.' AUTO_INCREMENT,
  NOME VARCHAR(50) 		NOT NULL COMMENT 'Nome do usuário.',
  PRIMARY KEY PK_USUARIO (ID),
  UNIQUE KEY UK_USUARIO_NOME (NOME)
) ENGINE=InnoDB COMMENT='Tabela para registro de usuário.';

CREATE TABLE kroton_test.SOLICITACAO(
  ID 					BIGINT(12) NOT NULL COMMENT 'Chave primária da tabela.' AUTO_INCREMENT,
  DT_ABERTURA 			DATE NOT NULL COMMENT 'Data da abertura.',
  DT_ULTIMA_ALTERACAO 	DATE NOT NULL COMMENT 'Data da útilma alteração.',
  DT_DEFERIMENTO 		DATE COMMENT 'Data do deferimento.',
  DT_AGUARDANDO_ANALISE DATE NOT NULL COMMENT 'Data de aguardando análise.',
  DT_PAGAMENTO 			DATE NOT NULL COMMENT 'Data do pagamento.',
  ID_SERVICO 			BIGINT(12) NOT NULL COMMENT 'Identificador da associação com a entidade SERVICO.',
  ID_STATUS 			BIGINT(12) NOT NULL COMMENT 'Identificador da associação com a entidade STATUS.',
  ID_CURSO 				BIGINT(12) NOT NULL COMMENT 'Identificador da associação com a entidade CURSO.',
  PRIMARY KEY PK_SOLICITACAO (ID)
) ENGINE=InnoDB COMMENT='Tabela para registro de Solicitações.';

ALTER TABLE kroton_test.SOLICITACAO 
	ADD CONSTRAINT 	
		FOREIGN KEY 	FK_SOLICITACAO_SERVICO (ID_SERVICO) 
		REFERENCES 		kroton_test.SERVICO(ID);

ALTER TABLE kroton_test.SOLICITACAO 
	ADD CONSTRAINT 	
		FOREIGN KEY 	FK_SOLICITACAO_STATUS (ID_STATUS) 
		REFERENCES 		kroton_test.STATUS(ID);


ALTER TABLE kroton_test.SOLICITACAO 
	ADD CONSTRAINT 	
		FOREIGN KEY 	FK_SOLICITACAO_CURSO (ID_CURSO) 
		REFERENCES 		kroton_test.CURSO(ID);
        

CREATE TABLE kroton_test.TRAMITE(
  ID 					BIGINT(12) NOT NULL COMMENT 'Chave primária da tabela.' AUTO_INCREMENT,
  ID_SEQUENCIA			BIGINT(12) NOT NULL COMMENT 'Sequencial reinciado a cada nova solicitacao.',
  DS_OBS 				VARCHAR(200) NOT NULL COMMENT 'Observação do tramite.',
  DT_TRAMITE 			DATETIME NOT NULL COMMENT 'Data do tramite.',
  ID_USUARIO 			BIGINT(12) NOT NULL COMMENT 'Identificador da associação com a entidade USUARIO.',
  ID_SOLICITACAO 		BIGINT(12) NOT NULL COMMENT 'Identificador da associação com a entidade SOLICITACAO.',
  PRIMARY KEY PK_TRAMITE (ID)
) ENGINE=InnoDB COMMENT='Tabela para registro de Tramites de Solicitações.';

ALTER TABLE kroton_test.TRAMITE 
	ADD CONSTRAINT 	
		FOREIGN KEY 	FK_TRAMITE_SOLICITACAO (ID_SOLICITACAO) 
		REFERENCES 		kroton_test.SOLICITACAO(ID);

ALTER TABLE kroton_test.TRAMITE 
	ADD CONSTRAINT 	
		FOREIGN KEY 	FK_TRAMITE_USUARIO (ID_USUARIO) 
		REFERENCES 		kroton_test.USUARIO(ID);
        
